import './styles.css'
export default function Search({ search, setSearch, handleSearch, weatherData }) {

  return (
    <div className="search-engine">      
      <input
        type="text"
        placeholder="Enter a city name"
        name="search"
        value={search}
        onChange={(event) => {setSearch(event.target.value); fetchWeatherData(event.target.value)}}
      />
      <button onClick={handleSearch} disabled={(weatherData) ? false : true}>Search</button>
    </div>
  );
}
